import React, { useState, useEffect } from 'react';
import 'exercitropo\client\src\styles\ExerciTropo.css';

const ExerciTropo = () => {
  const [index, setIndex] = useState(0);
  const [chatMessages, setChatMessages] = useState([
    {
      type: 'bot',
      message: "Piacere io sono un trainer metaforico. Il gioco consiste di vari livelli. In questa prima fase dovrai semplicemente descrivere le immagini, dettagliando cosa vedi, elencando le varie caratteristiche dei soggetti rappresentati... Io per adesso non esprimerò alcun giudizio, confermandoti se hai fatto bene o no",
    },
  ]);
  const [userInput, setUserInput] = useState('');
  const [loading, setLoading] = useState(false);

  const nextImage = async () => {
    setIndex(prevIndex => prevIndex + 1);
    setChatMessages([]);
    const response = await fetch(`/set_image?msg=${index + 1}`);
    const data = await response.json();
    setChatMessages([
      {
        type: 'bot',
        message: "In questa prima fase dovrai semplicemente descrivere le immagini, dettagliando cosa vedi, elencando le varie caratteristiche dei soggetti rappresentati... Io per adesso non esprimerò alcun giudizio, confermandoti se hai fatto bene o no",
      },
    ]);
  };

  const goBackImage = async () => {
    setIndex(prevIndex => prevIndex - 1);
    setChatMessages([]);
    const response = await fetch(`/set_again_image?msg=${index - 1}`);
    const data = await response.json();
    setChatMessages([
      {
        type: 'bot',
        message: "In questa prima fase dovrai semplicemente descrivere le immagini, dettagliando cosa vedi, elencando le varie caratteristiche dei soggetti rappresentati... Io per adesso non esprimerò alcun giudizio, confermandoti se hai fatto bene o no",
      },
    ]);
  };

  const getBotResponse = async () => {
    const rawText = userInput;
    setChatMessages([...chatMessages, { type: 'user', message: rawText }]);
    setUserInput('');
    setLoading(true);
    const response = await fetch(`/get?msg=${rawText}`);
    const data = await response.text();
    setChatMessages(prevMessages => [
      ...prevMessages,
      { type: 'bot', message: data },
    ]);
    setLoading(false);
  };

  const handleKeyPress = (e) => {
    if (e.which === 13) {
      getBotResponse();
    }
  };

  return (
    <div>
      <h1 align="center"><b>L'ExerciTropo</b></h1>
      <h4 align="center"><b>Questo è un training metaforico</b></h4>
      <div className="button-container">
        <button className="bottone" onClick={goBackImage}>Indietro</button>
        <button className="bottone" onClick={nextImage}>Avanti</button>
      </div>
      <p align="center">
        <img id="myImage" src={`static/${index}.png`} alt="immagine" height="424" width="581" />
      </p>
      <div className="boxed">
        <div id="chatbox">
          {chatMessages.map((msg, idx) => (
            <p key={idx} className={msg.type === 'bot' ? 'botText' : 'userText'}>
              <span>{msg.message}</span>
            </p>
          ))}
        </div>
        <div id="userInput">
          <input
            id="textInput"
            type="text"
            name="msg"
            placeholder="Message"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyPress={handleKeyPress}
          />
        </div>
        {loading && <img id="loadingGif" src="static/loading-4802_128.gif" alt="Loading..." />}
      </div>
    </div>
  );
};

export default ExerciTropo;
