import React, { useState, useEffect } from 'react';
import axios from 'axios';
import dizionari from '../pages/dizionari.json';
import { useParams } from 'react-router-dom';

const Task1 = () => {
  const { index: urlIndex } = useParams(); // Ottieni l'indice dalla URL
  const [inputMessage, setInputMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [showChat, setShowChat] = useState(false);
  const [initialMessage, setInitialMessage] = useState('');
  const [index, setIndex] = useState(Number(urlIndex) || 1); // Imposta l'indice iniziale

  useEffect(() => {
    if (urlIndex) {
      setIndex(Number(urlIndex)); // Aggiorna l'indice quando il parametro URL cambia
    }
  }, [urlIndex]);

  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    const newMessage = {
      sender: 'user',
      text: inputMessage.trim(),
    };
    setChatMessages(prevMessages => [...prevMessages, newMessage]);
    setInputMessage('');

    try {
      const botResponse = await axios.post('/chatbot', { message: inputMessage });
      const botMessage = {
        sender: 'bot',
        text: botResponse.data.message,
      };
      setChatMessages(prevMessages => [...prevMessages, botMessage]);
    } catch (error) {
      console.error('Error sending message to bot:', error);
    }
  };

  const handleShowChat = () => {
    setShowChat(true);

    const storiaKey = `storia_${index}`;
    const metaforaKey = `metafora_${index}`;

    // Controlla se la chiave esiste nel dizionario e recupera i dati
    const storiaMessage = dizionari[`training_${index}`] && dizionari[`training_${index}`][storiaKey] ? dizionari[`training_${index}`][storiaKey][0] : '';
    const metaforaMessage = dizionari[`training_${index}`] && dizionari[`training_${index}`][metaforaKey] ? dizionari[`training_${index}`][metaforaKey][0] : '';

    const initialSystemMessage = `Leggi bene la storia. Dopodiché dimmi per la metafora "${metaforaMessage}" quali sono le proprietà che accomunano entrambi i concetti e li rendono simili.`;

    setInitialMessage(initialSystemMessage);

    const systemMessage = {
      sender: 'system',
      text: initialSystemMessage,
    };
    setChatMessages(prevMessages => [...prevMessages, systemMessage]);
  };

  useEffect(() => {
    const chatBox = document.getElementById('chat-box');
    if (chatBox) {
      chatBox.scrollTop = chatBox.scrollHeight;
    }
  }, [chatMessages]);

  return (
    <div>
      <h1>This is Task1</h1>
      <p>{dizionari[`training_${index}`] && dizionari[`training_${index}`][`storia_${index}`] ? dizionari[`training_${index}`][`storia_${index}`][0] : 'No story available'}</p>

      {!showChat && (
        <button
          style={{ padding: '5px 10px', cursor: 'pointer', marginBottom: '10px' }}
          onClick={handleShowChat}
        >
          Ho letto
        </button>
      )}

      {showChat && (
        <div>
          <div id="chat-box" style={{ border: '1px solid #ccc', padding: '10px', minHeight: '200px', marginBottom: '10px', overflowY: 'auto' }}>
            {chatMessages.map((message, index) => (
              <div key={index} style={{ marginBottom: '5px', textAlign: message.sender === 'user' ? 'right' : 'left' }}>
                {message.sender === 'user' ? (
                  <span style={{ color: 'blue' }}>You:</span>
                ) : message.sender === 'bot' ? (
                  <span style={{ color: 'green' }}>Bot:</span>
                ) : (
                  <span style={{ color: 'black' }}>System:</span>
                )}{' '}
                {message.text}
              </div>
            ))}
          </div>

          <div style={{ display: 'flex' }}>
            <textarea
              style={{ flex: '1', marginRight: '10px', padding: '5px' }}
              placeholder="Type your message..."
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleSendMessage();
                }
              }}
            />
            <button
              style={{ padding: '5px 10px', cursor: 'pointer' }}
              onClick={handleSendMessage}
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Task1;
