import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Task2 = () => {
  const [images, setImages] = useState({ giusta: '', sbagliata: '' });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await fetch('/task2');
        const data = await response.json();
        setImages({
          giusta: data.giustaImagePath,
          sbagliata: data.sbagliataImagePath
        });
        setLoading(false);
      } catch (error) {
        console.error('Error fetching images:', error);
        setLoading(false);
      }
    };

    fetchImages();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ display: 'flex', gap: '10px' }}> {/* Flex container for horizontal layout */}
      <h1>Task2</h1>
      <p>Bisogna premere l'immagine che pi√π si alla metafora!</p>

      <Link to="/task3">
        <img src={images.giusta} alt="Giusta" style={{ width: '200px', height: 'auto', cursor: 'pointer' }} />
      </Link>
      <Link to="/task2errore">
        <img src={images.sbagliata} alt="Sbagliata" style={{ width: '200px', height: 'auto', cursor: 'pointer' }} />
      </Link>
    </div>
  );
};

export default Task2;
