import React, { useState, useEffect } from 'react';
import axios from 'axios';
import storia from '../pages/storia.json'; // Import the story JSON
import dizionari from '../pages/dizionari.json'; // Import the dictionaries JSON

const Task1 = () => {
  const [inputMessage, setInputMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [showChat, setShowChat] = useState(false);
  const [index, setIndex] = useState(1); // Use 1 as an example index, adjust as needed

  // Function to handle sending messages
  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    // Add user message to chat
    const newMessage = {
      sender: 'user',
      text: inputMessage.trim(),
    };
    setChatMessages(prevMessages => [...prevMessages, newMessage]);
    setInputMessage(''); // Clear input field

    try {
      // Send message to backend and get bot response
      const response = await axios.post('http://localhost:5000/chatbot', { message: inputMessage });
      const botMessage = {
        sender: 'bot',
        text: response.data.message,
      };
      setChatMessages(prevMessages => [...prevMessages, botMessage]);
    } catch (error) {
      console.error('Error sending message to bot:', error);
    }
  };

  // Function to handle showing the chat
  const handleShowChat = () => {
    setShowChat(true);

    // Construct dynamic keys for `storia` and `training`
    const storiaKey = `storia_${index}`;
    const metaforaKey = `metafora_${index}`;

    // Get the initial message from the dictionary
    const storiaMessage = dizionari.training_1[storiaKey] ? dizionari.training_1[storiaKey][0] : '';
    const metaforaMessage = dizionari.training_1[metaforaKey] ? dizionari.training_1[metaforaKey][0] : '';

    // Construct the initial system message
    const initialSystemMessage = `Leggi bene la storia. Dopodiché dimmi per la metafora "${metaforaMessage}" quali sono le proprietà che accomunano entrambi i concetti e li rendono simili.`;

    // Set the initial message state
    setInitialMessage(initialSystemMessage);

    const systemMessage = {
      sender: 'system',
      text: initialSystemMessage,
    };
    setChatMessages(prevMessages => [...prevMessages, systemMessage]);
  };

  // Effect to scroll to bottom of chat box on update
  useEffect(() => {
    const chatBox = document.getElementById('chat-box');
    if (chatBox) {
      chatBox.scrollTop = chatBox.scrollHeight;
    }
  }, [chatMessages]);

  return (
    <div>
      <h1>This is Task1</h1>
      <p>{storia[`storia_${index}`]}</p> {/* Display the story from JSON */}

      {!showChat && (
        <button
          style={{ padding: '5px 10px', cursor: 'pointer', marginBottom: '10px' }}
          onClick={handleShowChat}
        >
          Ho letto
        </button>
      )}

      {showChat && (
        <div>
          <div id="chat-box" style={{ border: '1px solid #ccc', padding: '10px', minHeight: '200px', marginBottom: '10px', overflowY: 'auto' }}>
            {chatMessages.map((message, index) => (
              <div key={index} style={{ marginBottom: '5px', textAlign: message.sender === 'user' ? 'right' : 'left' }}>
                {message.sender === 'user' ? (
                  <span style={{ color: 'blue' }}>You:</span>
                ) : (
                  <span style={{ color: 'green' }}>Bot:</span>
                )}{' '}
                {message.text}
              </div>
            ))}
          </div>

          <div style={{ display: 'flex' }}>
            <textarea
              style={{ flex: '1', marginRight: '10px', padding: '5px' }}
              placeholder="Type your message..."
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleSendMessage();
                }
              }}
            />
            <button
              style={{ padding: '5px 10px', cursor: 'pointer' }}
              onClick={handleSendMessage}
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Task1;
