import React from 'react';
import { Link, useParams } from 'react-router-dom';

const Task2 = () => {
  const { index } = useParams();
  console.log('Current index:', index); // Debug line

  const giustaImagePath = `/images/${index}/giusta_${index}.png`;
  const sbagliataImagePath = `/images/${index}/sbagliata_${index}.png`;

  return (
    <div>
      <h1>Task2</h1>
      <p>Bisogna premere l'immagine che pi√π si alla metafora!</p>

      <Link to="/task3">
        <img src={giustaImagePath} alt={`Giusta ${index}`} style={{ width: '200px', height: 'auto' }} />
      </Link>
      <Link to="/task2errore">
        <img src={sbagliataImagePath} alt={`Sbagliata ${index}`} style={{ width: '200px', height: 'auto' }} />
      </Link>
    </div>
  );
};

export default Task2;
