import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Instructions = () => {
  const location = useLocation();
  const { index } = location.state || {};

  return (
    <div>
      <h1>Instructions</h1>
      <p>Welcome to the instructions page!</p>
      {index && (
        <Link to={`/task1/${index}`}>
          <button>Start</button>
        </Link>
      )}
    </div>
  );
};

export default Instructions;
