import React from 'react';
import images from '..publc/images'
import { Link, useParams } from 'react-router-dom';

const Task2 = () => {
  const { index } = useParams();
  const giustaImagePath = `/images/${index}/giusta_${index}.png`;
  const sbagliataImagePath = `/images/${index}/sbagliata_${index}.png`;

  return (
    <div style={{ display: 'flex', gap: '10px' }}> {/* Flex container for horizontal layout */}
      <h1>Task2</h1>
      <p>Bisogna premere l'immagine che pi√π si alla metafora!</p>

      <Link to="/task3">
        <img src={giustaImagePath} alt={`Giusta ${index}`} style={{ width: '200px', height: 'auto', cursor: 'pointer' }} />
      </Link>
      <Link to="/task2errore">
        <img src={sbagliataImagePath} alt={`Sbagliata ${index}`} style={{ width: '200px', height: 'auto', cursor: 'pointer' }} />
      </Link>
    </div>
  );
};

export default Task2;
