import React, { useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Home from "./pages/Home";
import Instructions from "./pages/Instructions"; // Importa il componente per le istruzioni
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';

function App() {

  const MyNavbar = () => {
    return (
      <Navbar bg="light" expand="lg">
        <Navbar.Brand as={Link} to="/">METACOM</Navbar.Brand> {/* Usa Link per navigare alla home */}
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link as={Link} to="/">Home</Nav.Link> {/* Usa Link per navigare alla home */}
            <Nav.Link as={Link} to="/instructions">Instructions</Nav.Link> {/* Link per le istruzioni */}
            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
              <NavDropdown.Divider />
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  };

  const [projectFilepath, setProjectFilepath] = useState('');
  const [serverResponse, setServerResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send data to the backend
      const response = await axios.post('/get_answer', { projectFilepath });
      setServerResponse(response.data);
      console.log('Text sent successfully!', response, response.data);
    } catch (error) {
      console.error('Error sending text:', error);
    }
  };

  return (
    <Router>
      <div>
        <MyNavbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/instructions" element={<Instructions />} /> {/* Aggiungi la route per le istruzioni */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
