import React, { useState } from 'react';
import axios from 'axios';

const Home = () => {
  const [passwordInput, setPasswordInput] = useState('');
  const [indexInput, setIndexInput] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [indexMessage, setIndexMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/login', { inputString: passwordInput });
      if (response.data.authenticated) {
        setIsAuthenticated(true);
        setLoginError('');
      } else {
        setIsAuthenticated(false);
        setLoginError('Invalid credentials');
      }
    } catch (error) {
      console.error(error);
      setLoginError('An error occurred. Please try again.');
    }
  };

  const handleIndex = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/set_index', { inputIndex: indexInput });
      setIndexMessage(response.data.status);
    } catch (error) {
      console.error(error);
      setIndexMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      {/* Login Section */}
      <section style={{ marginBottom: '40px' }}>
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            value={passwordInput}
            onChange={(e) => setPasswordInput(e.target.value)}
            placeholder="Enter your password"
            required
            style={{ marginRight: '10px' }}
          />
          <button type="submit">Login</button>
        </form>
        {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
        {isAuthenticated && <p style={{ color: 'green' }}>Login successful!</p>}
      </section>

      {/* Set Index Section */}
      <section>
        <h1>Set Index</h1>
        <form onSubmit={handleIndex}>
          <input
            type="text"
            value={indexInput}
            onChange={(e) => setIndexInput(e.target.value)}
            placeholder="Enter your index"
            required
            style={{ marginRight: '10px' }}
          />
          <button type="submit">Set Index</button>
        </form>
        {indexMessage && <p>{indexMessage}</p>}
      </section>
    </div>
  );
};

export default Home;
