import React, { useState, useEffect } from 'react';
import axios from 'axios';
import dizionari from '../pages/dizionari.json';

const ExerciTropo = () => {
  const [inputMessage, setInputMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [imageSrc, setImageSrc] = useState(`static/${currentImageIndex}.png`);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // When the component mounts or image index changes, fetch the initial bot message
    fetchInitialBotMessage(currentImageIndex);
  }, [currentImageIndex]);

  const fetchInitialBotMessage = async (index) => {
    try {
      const response = await axios.get(`/set_image`, { params: { msg: index } });
      const initialBotMessage = "In questa prima fase dovrai semplicemente descrivere le immagini, dettagliando cosa vedi, elencando le varie caratteristiche dei soggetti rappresentati... Io per adesso non esprimerò alcun giudizio, confermandoti se hai fatto bene o no";
      setChatMessages([{ sender: 'bot', text: initialBotMessage }]);
    } catch (error) {
      console.error('Error fetching initial bot message:', error);
    }
  };

  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    const newMessage = {
      sender: 'user',
      text: inputMessage.trim(),
    };
    setChatMessages(prevMessages => [...prevMessages, newMessage]);
    setInputMessage('');

    setLoading(true);
    try {
      const response = await axios.get('/get', { params: { msg: inputMessage } });
      const botMessage = {
        sender: 'bot',
        text: response.data,
      };
      setChatMessages(prevMessages => [...prevMessages, botMessage]);
    } catch (error) {
      console.error('Error sending message to bot:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleNextImage = () => {
    setCurrentImageIndex(prevIndex => prevIndex + 1);
  };

  const handlePrevImage = () => {
    if (currentImageIndex > 0) {
      setCurrentImageIndex(prevIndex => prevIndex - 1);
    }
  };

  useEffect(() => {
    setImageSrc(`static/${currentImageIndex}.png`);
  }, [currentImageIndex]);

  return (
    <div>
      <h1 align="center"><b>L'ExerciTropo</b></h1>
      <h4 align="center"><b>Questo è un training metaforico</b></h4>

      <div className="button-container" style={{ textAlign: 'center', marginBottom: '20px' }}>
        <button className="bottone" onClick={handlePrevImage}>Indietro</button>
        <button className="bottone" onClick={handleNextImage}>Avanti</button>
      </div>

      <div align="center">
        <img id="myImage" src={imageSrc} alt="immagine" height="424" width="581" />
      </div>

      <div className="chat-container" style={{ marginTop: '20px', textAlign: 'center' }}>
        <div id="chat-box" style={{ border: '1px solid #ccc', padding: '10px', minHeight: '200px', marginBottom: '10px', overflowY: 'auto', maxHeight: '300px' }}>
          {chatMessages.map((message, index) => (
            <div key={index} style={{ marginBottom: '5px', textAlign: message.sender === 'user' ? 'right' : 'left' }}>
              {message.sender === 'user' ? (
                <span style={{ color: 'blue' }}>You:</span>
              ) : (
                <span style={{ color: 'green' }}>Bot:</span>
              )}{' '}
              {message.text}
            </div>
          ))}
        </div>

        <div style={{ display: 'flex', justifyContent: 'center' }}>
          <textarea
            style={{ flex: '1', marginRight: '10px', padding: '5px', width: '60%' }}
            placeholder="Type your message..."
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage();
              }
            }}
          />
          <button
            style={{ padding: '5px 10px', cursor: 'pointer' }}
            onClick={handleSendMessage}
          >
            Send
          </button>
        </div>
      </div>
      {loading && <img id="loadingGif" src="static/loading-4802_128.gif" alt="Loading..." style={{ display: 'block', margin: '0 auto' }} />}
    </div>
  );
};

export default ExerciTropo;
