import React, { useState, useEffect } from 'react';
import axios from 'axios';
import dizionari from '../pages/dizionari.json'; // Importa il JSON dei dizionari

const Task1 = () => {
  const [inputMessage, setInputMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [showChat, setShowChat] = useState(false);
  const [index, setIndex] = useState(1); // Usa lo stato per gestire l'indice

  // Funzione per inviare messaggi
  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    const newMessage = {
      sender: 'user',
      text: inputMessage.trim(),
    };
    setChatMessages(prevMessages => [...prevMessages, newMessage]);
    setInputMessage('');

    try {
      // Verifica che l'URL del backend sia corretto
      const response = await axios.post('http://localhost:5000/chatbot', { message: inputMessage });
      const botMessage = {
        sender: 'bot',
        text: response.data.message,
      };
      setChatMessages(prevMessages => [...prevMessages, botMessage]);
    } catch (error) {
      console.error('Error sending message to bot:', error);
    }
  };

  // Funzione per mostrare la chat
  const handleShowChat = () => {
    setShowChat(true);

    const trainingKey = `training_${index}`;
    const metaforaKey = `metafora_${index}`;

    const trainingData = dizionari[trainingKey] || {};
    const metaforaMessage = trainingData[metaforaKey]?.[0] || '';

    const initialSystemMessage = `Adesso prova a trovare le proprietà che accomunano i concetti di questa metafora: "${metaforaMessage}"`;

    const systemMessage = {
      sender: 'system',
      text: initialSystemMessage,
    };
    setChatMessages(prevMessages => [...prevMessages, systemMessage]);
  };

  useEffect(() => {
    const chatBox = document.getElementById('chat-box');
    if (chatBox) {
      chatBox.scrollTop = chatBox.scrollHeight;
    }
  }, [chatMessages]);

  return (
    <div>
      <h1>This is Task1</h1>
      <p>Leggi bene la storia: "{dizionari[`training_${index}`]?.[`storia_${index}`]?.[0] || ''}"</p>

      {!showChat && (
        <button
          style={{ padding: '5px 10px', cursor: 'pointer', marginBottom: '10px' }}
          onClick={handleShowChat}
        >
          Ho letto
        </button>
      )}

      {showChat && (
        <div>
          <div id="chat-box" style={{ border: '1px solid #ccc', padding: '10px', minHeight: '200px', marginBottom: '10px', overflowY: 'auto' }}>
            {chatMessages.map((message, index) => (
              <div key={index} style={{ marginBottom: '5px', textAlign: message.sender === 'user' ? 'right' : 'left' }}>
                {message.sender === 'user' ? (
                  <span style={{ color: 'blue' }}>You:</span>
                ) : (
                  <span style={{ color: 'green' }}>Bot:</span>
                )}{' '}
                {message.text}
              </div>
            ))}
          </div>

          <div style={{ display: 'flex' }}>
            <textarea
              style={{ flex: '1', marginRight: '10px', padding: '5px' }}
              placeholder="Type your message..."
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleSendMessage();
                }
              }}
            />
            <button
              style={{ padding: '5px 10px', cursor: 'pointer' }}
              onClick={handleSendMessage}
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Task1;
