import React, {useState, useEffect} from 'react'

import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "./pages/Home"
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';


function App(){


  const MyNavbar = () => {
    return (
      <Navbar bg="light" expand="lg">
        <Navbar.Brand href="#home">BrandName</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#link">Link</Nav.Link>
            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
              <NavDropdown.Divider />
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  };

  const [data, setData] = useState([{}])

  const [projectFilepath, setProjectFilepath] = useState(''); // projectfileecc contiene l'input dato dall'utente
  const [serverResponse, setServerResponse] = useState('');

  const handleUserResponse = async (e) => {
    e.preventDefault();

    const userResponse = e.target.userResponse.value;  // Assume che il valore dell'input sia accessibile così
    const validResponses = ['yes', 'no', 'maybe'];  // Lista di risposte valide

    try {
        if (validResponses.includes(userResponse.toLowerCase())) {
            console.log(`User response: ${userResponse}`);
            const response = await axios.post('/validate_response', { userResponse });
            console.log(response);
            setServerResponse(response.data);
            console.log('Response sent successfully!', response, response.data);
        } else {
            console.log('Invalid response:', userResponse);
            setServerResponse({ error: 'Invalid response' });
        }
    } catch (error) {
        console.error('Error sending response:', error);
    }
};


  return (
    <Router>
      <div>
        <MyNavbar />
        <Routes>
          <Route path="/" element={<Home />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App
