import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ExerciTropo() {
  const { index } = useParams();
  const [giustaImagePath, setGiustaImagePath] = useState('');
  const [sbagliataImagePath, setSbagliataImagePath] = useState('');

  useEffect(() => {
    const giustaImage = `/images/${index}/giusta_${index}.png`;
    const sbagliataImage = `/images/${index}/sbagliata_${index}.png`;

    setGiustaImagePath(giustaImage);
    setSbagliataImagePath(sbagliataImage);
  }, [index]);

  return (
    <div>
      <h2>ExerciTropo Step {index}</h2>
      {giustaImagePath && (
        <div>
          <h3>Giusta</h3>
          <img src={giustaImagePath} alt="Giusta" />
        </div>
      )}
      {sbagliataImagePath && (
        <div>
          <h3>Sbagliata</h3>
          <img src={sbagliataImagePath} alt="Sbagliata" />
        </div>
      )}
    </div>
  );
}

export default ExerciTropo;
