import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [inputString, setInputString] = useState('');
  const [inputIndex, setInputIndex] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/login', { inputString, inputIndex });

      if (response.data.authenticated) {
        setError('');
        // Navigate to Instructions page with the index
        navigate(`/instructions/${inputIndex}`);
      } else {
        setError('Login Errato: controlla le tue credenziali o di aver inserito il numero corretto del trial.');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          value={inputString}
          onChange={(e) => setInputString(e.target.value)}
          placeholder="Enter your password"
          required
        />
        <input
          type="text"
          value={inputIndex}
          onChange={(e) => setInputIndex(e.target.value)}
          placeholder="Enter your index"
          required
        />
        <button type="submit">Login</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default Home;
