import React, { useState, useEffect } from 'react';
import axios from 'axios';
import storia from '../pages/storia.json'; // Importa il file JSON della storia
import dizionari from '../pages/dizionari.json'; // Importa il file JSON dei dizionari

const Task1 = () => {
  const [inputMessage, setInputMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [showChat, setShowChat] = useState(false);
  const [index, setIndex] = useState(1); // Utilizziamo 1 come esempio, puoi modificarlo come necessario
  const [initialMessage, setInitialMessage] = useState('');

  // Funzione per gestire l'invio dei messaggi
  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    // Aggiungi il messaggio dell'utente alla chat
    const newMessage = {
      sender: 'user',
      text: inputMessage.trim(),
    };
    setChatMessages(prevMessages => [...prevMessages, newMessage]);
    setInputMessage(''); // Pulisce il campo di input

    try {
      // Invia il messaggio al backend e ottieni la risposta del bot
      const botResponse = await axios.post('/chatbot', { message: inputMessage });
      const botMessage = {
        sender: 'bot',
        text: botResponse.data.message,
      };
      setChatMessages(prevMessages => [...prevMessages, botMessage]);
    } catch (error) {
      console.error('Errore nell\'invio del messaggio al bot:', error);
    }
  };

  // Funzione per gestire la visualizzazione del chatbot
  const handleShowChat = () => {
    setShowChat(true);
    // Ottieni il messaggio iniziale dal dizionario
    const message = dizionari.training_1[`storia_${index}`] ? dizionari.training_1[`storia_${index}`][0] : '';
    setInitialMessage(message);

    const systemMessage = {
      sender: 'system',
      text: message,
    };
    setChatMessages(prevMessages => [...prevMessages, systemMessage]);
  };

  // Effetto per scorrere verso il basso nella finestra della chat quando viene aggiornato
  useEffect(() => {
    const chatBox = document.getElementById('chat-box');
    if (chatBox) {
      chatBox.scrollTop = chatBox.scrollHeight;
    }
  }, [chatMessages]);

  return (
    <div>
      <h1>This is Task1</h1>
      <p>{storia[`storia_${index}`]}</p> {/* Visualizza la storia dal JSON */}

      {!showChat && (
        <button
          style={{ padding: '5px 10px', cursor: 'pointer', marginBottom: '10px' }}
          onClick={handleShowChat}
        >
          Ho letto
        </button>
      )}

      {showChat && (
        <div>
          <div id="chat-box" style={{ border: '1px solid #ccc', padding: '10px', minHeight: '200px', marginBottom: '10px', overflowY: 'auto' }}>
            {chatMessages.map((message, index) => (
              <div key={index} style={{ marginBottom: '5px', textAlign: message.sender === 'user' ? 'right' : 'left' }}>
                {message.sender === 'user' ? (
                  <span style={{ color: 'blue' }}>You:</span>
                ) : message.sender === 'bot' ? (
                  <span style={{ color: 'green' }}>Bot:</span>
                ) : (
                  <span style={{ color: 'black' }}>Bot:</span>
                )}{' '}
                {message.text}
              </div>
            ))}
          </div>

          <div style={{ display: 'flex' }}>
            <textarea
              style={{ flex: '1', marginRight: '10px', padding: '5px' }}
              placeholder="Type your message..."
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleSendMessage();
                }
              }}
            />
            <button
              style={{ padding: '5px 10px', cursor: 'pointer' }}
              onClick={handleSendMessage}
            >
              Send
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Task1;
