import React, {useState, useEffect} from 'react'
import ImageSlider from "./ImageSlider";
import laraImg from "./images/Lara.jpg";
import iconImg from "./images/musicarteIcon.png";
import queenImg from "./images/Queen.png";
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "./pages/Home"
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';


function App(){

  const images = [
    laraImg,
    iconImg,
    queenImg,
  ];




  const [data, setData] = useState([{}])

  const [projectFilepath, setProjectFilepath] = useState(''); // projectfileecc contiene l'input dato dall'utente
  const [serverResponse, setServerResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send data to the backend
      console.log(projectFilepath)
      const response = await axios.post('/get_answer', { projectFilepath });
      console.log(response)
      setServerResponse(response.data);
      console.log('Text sent successfully!', response, response.data);
    } catch (error) {
      console.error('Error sending text:', error);
    }
  };

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={<Home />} />

        </Routes>
      </div>
    </Router>
  );
}

export default App
