import React from 'react';
import { Link, useParams } from 'react-router-dom';

const Task2 = () => {
  const { index } = useParams();
  const giustaImagePath = `/images/${index}/giusta_${index}.png`;
  const sbagliataImagePath = `/images/${index}/sbagliata_${index}.png`;

  return (
    <div>
      <h1>Task2</h1>
      <p>Bisogna premere l'immagine che pi√π si alla metafora!</p>

      <div style={{ display: 'flex', justifyContent: 'space-around', alignItems: 'center' }}>
        <Link to="/task3">
          <img
            src={giustaImagePath}
            alt={`Giusta ${index}`}
            style={{ cursor: 'pointer', width: '200px', height: 'auto' }}
          />
        </Link>
        <Link to="/task2errore">
          <img
            src={sbagliataImagePath}
            alt={`Sbagliata ${index}`}
            style={{ cursor: 'pointer', width: '200px', height: 'auto' }}
          />
        </Link>
      </div>
    </div>
  );
};

export default Task2;
