import React, {useState, useEffect} from 'react'

import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "./pages/Home"
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';


function App(){


  const MyNavbar = () => {
    return (
      <Navbar bg="light" expand="lg">
        <Navbar.Brand href="#home">Boh</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#home">Boh </Nav.Link>
            <Nav.Link href="#link">Boh2</Nav.Link>
            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
              <NavDropdown.Divider />
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  };

  const [data, setData] = useState([{}])

  const [projectFilepath, setProjectFilepath] = useState(''); // projectfileecc contiene l'input dato dall'utente
  const [serverResponse, setServerResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send data to the backend
      console.log(projectFilepath)
      const response = await axios.post('/get_answer', { projectFilepath });
      console.log(response)
      setServerResponse(response.data);
      console.log('Text sent successfully!', response, response.data);
    } catch (error) {
      console.error('Error sending text:', error);
    }
  };

  return (
    <Router>
      <div>
        <MyNavbar />
        <Routes>
          <Route path="/" element={<Home />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App
