import React, { useState, useEffect } from 'react';
import '../styles/ExerciTropo.css';

// Import images
import image0 from '../images/0.png';
import image1 from '../images/1.png';

const imageMap = {
  0: image0,
  1: image1,
};

const ExerciTropo = () => {
  const [index, setIndex] = useState(0);
  const [chatMessages, setChatMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Function to initialize chat messages
  const initializeChat = () => {
    setChatMessages([
      {
        type: 'bot',
        message: "Piacere io sono un trainer metaforico. Il gioco consiste di vari livelli. In questa prima fase dovrai semplicemente descrivere le immagini...",
      },
    ]);
  };

  // Initialize chat messages and fetch initial bot response when index changes
  useEffect(() => {
    initializeChat();
    fetchBotResponse("In questa fase dovrai semplicemente descrivere le immagini...");
  }, [index]);

  // Function to fetch bot response
  const fetchBotResponse = async (message) => {
    try {
      const response = await fetch('/chatbot', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message, index }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Bot response:', data); // Debugging line
      setChatMessages(prevMessages => [
        ...prevMessages,
        { type: 'bot', message: data.message },
      ]);
    } catch (error) {
      console.error('Error fetching bot response:', error);
      setChatMessages(prevMessages => [
        ...prevMessages,
        { type: 'bot', message: "Sorry, there was an error processing your request." },
      ]);
    }
  };

  // Handle the "Avanti" button click
  const nextImage = async () => {
    const newIndex = index + 1;
    setIndex(newIndex);
  };

  // Handle the "Indietro" button click
  const goBackImage = async () => {
    if (index > 0) {
      const newIndex = index - 1;
      setIndex(newIndex);
    }
  };

  // Get bot response for user input
  const getBotResponse = async () => {
    const rawText = userInput;
    setChatMessages([...chatMessages, { type: 'user', message: rawText }]);
    setUserInput('');
    setLoading(true);

    try {
      await fetchBotResponse(rawText);
    } finally {
      setLoading(false);
    }
  };

  // Handle key press for input
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      getBotResponse();
    }
  };

  return (
    <div>
      <h1 className="title">L'ExerciTropo</h1>
      <h4 className="subtitle">Questo è un training metaforico</h4>
      <div className="button-container">
        <button className="bottone" onClick={goBackImage} disabled={index === 0}>Indietro</button>
        <button className="bottone" onClick={nextImage}>Avanti</button>
      </div>
      <div className="image-container">
        <img
          id="myImage"
          src={imageMap[index]} // Use the image based on index
          alt={`immagine ${index}`}
          style={{ width: '100%', height: 'auto' }}
        />
      </div>
      <div className="boxed">
        <div id="chatbox">
          {chatMessages.map((msg, idx) => (
            <p key={idx} className={msg.type === 'bot' ? 'botText' : 'userText'}>
              <span>{msg.message}</span>
            </p>
          ))}
        </div>
        <div id="userInput">
          <input
            id="textInput"
            type="text"
            name="msg"
            placeholder="Message"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyPress={handleKeyPress}
          />
        </div>
        {loading && <img id="loadingGif" src="/images/loading-4802_128.gif" alt="Loading..." />}
      </div>
    </div>
  );
};

export default ExerciTropo;
