import React, { useState } from 'react';
import Slider from 'react-slick';
import axios from 'axios'; // Make sure you have Axios installed
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const ImageSlider = ({ images }) => {
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);

  const handleImageClick = (index) => {
    setSelectedImageIndex(index);
    // Send the index to the backend
    sendIndexToBackend(index);
  };

  const sendIndexToBackend = async (index) => {
    try {
      await axios.post('/get_image_index', { index }); 
      console.log('Index sent successfully:', index);
    } catch (error) {
      console.error('Error sending index:', error);
    }
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 2000,
    cssEase: 'linear',
  };

  return (
    <div className="image-slider">
      <Slider {...settings}>
        {images.map((image, index) => (
          <div key={index} onClick={() => handleImageClick(index)}>
            <img src={image} alt={`Slide ${index}`} />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default ImageSlider;
