import React, {useState, useEffect} from 'react'
import ImageSlider from "./ImageSlider";
import laraImg from "./images/Lara.jpg";
import iconImg from "./images/musicarteIcon.png";
import queenImg from "./images/Queen.png";
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from "./pages/Home"
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';


function App(){

  const images = [
    laraImg,
    iconImg,
    queenImg,
  ];


  const MyNavbar = () => {
    return (
      <Navbar bg="light" expand="lg">
        <Navbar.Brand href="#home">BrandName</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#link">Link</Nav.Link>
            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  };

  const [data, setData] = useState([{}])

  const [projectFilepath, setProjectFilepath] = useState(''); // projectfileecc contiene l'input dato dall'utente
  const [serverResponse, setServerResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send data to the backend
      console.log(projectFilepath)
      const response = await axios.post('/get_answer', { projectFilepath });
      console.log(response)
      setServerResponse(response.data);
      console.log('Text sent successfully!', response, response.data);
    } catch (error) {
      console.error('Error sending text:', error);
    }
  };

  return (
    <Router>
      <div>
        //<MyNavbar />
        <Routes>
          <Route path="/" element={<Home />} />

        </Routes>
      </div>
    </Router>
  );
}

export default App
