import React, { useState, useEffect, useCallback } from 'react';
import '../styles/ExerciTropo.css';

const ExerciTropo = () => {
  const [index, setIndex] = useState(0);
  const [chatMessages, setChatMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Initialize chat with a welcome message
  const initializeChat = useCallback(() => {
    setChatMessages([
      {
        type: 'bot',
        message: "Piacere io sono un trainer metaforico. Il gioco consiste di vari livelli. In questa prima fase dovrai semplicemente descrivere le immagini...",
      },
    ]);
  }, []);

  // Fetch bot response
  const fetchBotResponse = useCallback(async (message) => {
    try {
      const response = await fetch('/chatbot', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message, index }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setChatMessages(prevMessages => [
        ...prevMessages,
        { type: 'bot', message: data.message },
      ]);
    } catch (error) {
      console.error('Error fetching bot response:', error);
      setChatMessages(prevMessages => [
        ...prevMessages,
        { type: 'bot', message: "Sorry, there was an error processing your request." },
      ]);
    }
  }, [index]);

  // Update image source based on index
  const updateImageSource = (newIndex) => {
    const imageElement = document.getElementById('myImage');
    if (imageElement) {
      imageElement.src = `static/${newIndex}.png`;
    }
  };

  // Handle image change
  const handleImageChange = async (newIndex) => {
    setIndex(newIndex);
    setChatMessages([]); // Clear chat messages

    try {
      const endpoint = newIndex >= index ? `/set_image?msg=${newIndex}` : `/set_again_image?msg=${newIndex}`;
      const response = await fetch(endpoint);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      await response.text();

      // Update the image source and fetch the bot response
      updateImageSource(newIndex);
      initializeChat();
      fetchBotResponse("In questa fase dovrai semplicemente descrivere le immagini...");
    } catch (error) {
      console.error('Error changing image:', error);
      setChatMessages(prevMessages => [
        ...prevMessages,
        { type: 'bot', message: "Sorry, there was an error processing your request." },
      ]);
    }
  };

  // Handle button clicks
  const nextImage = () => {
    handleImageChange(index + 1);
  };

  const goBackImage = () => {
    if (index > 0) {
      handleImageChange(index - 1);
    }
  };

  // Get bot response for user input
  const getBotResponse = async () => {
    const rawText = userInput.trim();
    if (!rawText) return;

    setChatMessages(prevMessages => [...prevMessages, { type: 'user', message: rawText }]);
    setUserInput('');
    setLoading(true);

    try {
      await fetchBotResponse(rawText);
    } finally {
      setLoading(false);
    }
  };

  // Handle key press for input
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      getBotResponse();
    }
  };

  useEffect(() => {
    initializeChat();
  }, [initializeChat]);

  return (
    <div>
      <h1 className="title">L'ExerciTropo</h1>
      <h4 className="subtitle">Questo Ã¨ un training metaforico</h4>
      <div className="button-container">
        <button className="bottone" onClick={goBackImage} disabled={index === 0}>Indietro</button>
        <button className="bottone" onClick={nextImage}>Avanti</button>
      </div>
      <div className="image-container">
        <img
          id="myImage"
          src={`static/${index}.png`}
          alt={`immagine ${index}`}
          style={{ width: '100%', height: 'auto' }}
        />
      </div>
      <div className="boxed">
        <div id="chatbox">
          {chatMessages.map((msg, idx) => (
            <p key={idx} className={msg.type === 'bot' ? 'botText' : 'userText'}>
              <span>{msg.message}</span>
            </p>
          ))}
        </div>
        <div id="userInput">
          <input
            id="textInput"
            type="text"
            name="msg"
            placeholder="Message"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyPress={handleKeyPress}
          />
        </div>
        {loading && <img id="loadingGif" src="/images/loading-4802_128.gif" alt="Loading..." />}
      </div>
    </div>
  );
};

export default ExerciTropo;
