import React, { useState, useEffect } from 'react';
import axios from 'axios';
import storia from './pages/storia.json';  // Correctly import the JSON file

const Task1 = () => {
  const [inputMessage, setInputMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);

  // Fetch initial bot message when the component mounts
  useEffect(() => {
    const fetchInitialMessage = async () => {
      try {
        const botResponse = await axios.post('/chatbot', { message: '' });
        const botMessage = {
          sender: 'bot',
          text: botResponse.data.message,
        };
        setChatMessages([botMessage]); // Set initial bot message
      } catch (error) {
        console.error('Error fetching initial bot message:', error);
      }
    };

    fetchInitialMessage();
  }, []);

  // Function to handle sending messages
  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    // Add user message to chat
    const newMessage = {
      sender: 'user',
      text: inputMessage.trim(),
    };
    setChatMessages((prevMessages) => [...prevMessages, newMessage]); // Append new message to existing messages
    setInputMessage(''); // Clear input field

    try {
      // Send message to backend and get bot response
      const botResponse = await axios.post('/chatbot', { message: inputMessage });
      const botMessage = {
        sender: 'bot',
        text: botResponse.data.message,
      };
      setChatMessages((prevMessages) => [...prevMessages, botMessage]); // Append bot response to existing messages
    } catch (error) {
      console.error('Error sending message to bot:', error);
    }
  };

  // Effect to scroll to bottom of chat box on update
  useEffect(() => {
    const chatBox = document.getElementById('chat-box');
    if (chatBox) {
      chatBox.scrollTop = chatBox.scrollHeight;
    }
  }, [chatMessages]);

  return (
    <div>
      <h1>This is Task1</h1>
      <p>Leggi la storia e rispondi alle domande del bot!</p>

      <div id="chat-box" style={{ border: '1px solid #ccc', padding: '10px', minHeight: '200px', marginBottom: '10px', overflowY: 'auto' }}>
        {chatMessages.map((message, index) => (
          <div key={index} style={{ marginBottom: '5px', textAlign: message.sender === 'user' ? 'right' : 'left' }}>
            {message.sender === 'user' ? (
              <span style={{ color: 'blue' }}>You:</span>
            ) : (
              <span style={{ color: 'green' }}>Bot:</span>
            )}{' '}
            {message.text}
          </div>
        ))}
      </div>

      <div style={{ display: 'flex' }}>
        <textarea
          style={{ flex: '1', marginRight: '10px', padding: '5px' }}
          placeholder="Type your message..."
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSendMessage();
            }
          }}
        />
        <button
          style={{ padding: '5px 10px', cursor: 'pointer' }}
          onClick={handleSendMessage}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default Task1;
