import React, { useState } from 'react';
import axios from 'axios';
import { Redirect } from 'react-router-dom';

const Home = () => {
  const [inputString, setInputString] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [error, setError] = useState('');
  const [redirectToInstructions, setRedirectToInstructions] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/login', { inputString });
      if (response.data.authenticated) {
        setIsAuthenticated(true);
        setError('');
        setRedirectToInstructions(true);
      } else {
        setIsAuthenticated(false);
        setError('Invalid credentials');
      }
    } catch (error) {
      console.error(error);
      setError('An error occurred. Please try again.');
    }
  };

  if (redirectToInstructions) {
    return <Redirect to="/instructions" />;
  }

  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          value={inputString}
          onChange={(e) => setInputString(e.target.value)}
          placeholder="Enter your string"
          required
        />
        <button type="submit">Login</button>
      </form>
      {error && <p>{error}</p>}
      {isAuthenticated && <p>Login successful!</p>}
    </div>
  );
};

export default Home;
