// src/pages/Task2.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Task2 = () => {
  const [index, setIndex] = useState(null);

  useEffect(() => {
    const fetchIndex = async () => {
      try {
        const response = await axios.get('/api/getIndex'); // Adjust the API endpoint as needed
        setIndex(response.data.index);
      } catch (error) {
        console.error('Error fetching index:', error);
      }
    };

    fetchIndex();
  }, []);

  if (index === null) {
    return <div>Loading...</div>;
  }

  const giustaImagePath = `/images/${index}/giusta_${index}.png`;
  const sbagliataImagePath = `/images/${index}/sbagliata_${index}.png`;

  return (
    <div>
      <h1>Task2</h1>
      <p>Bisogna premere l'immagine che pi√π si alla metafora!</p>
        
      <Link to="/Task3">
        <img src={giustaImagePath} alt="Giusta" style={{ cursor: 'pointer' }} />
      </Link>
      <Link to="/Task2errore">
        <img src={sbagliataImagePath} alt="Sbagliata" style={{ cursor: 'pointer' }} />
      </Link>
    </div>
  );
};

export default Task2;
