import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';

const Task2 = () => {
  const { index } = useParams();
  const [imagePaths, setImagePaths] = useState({
    giustaImagePath: '',
    sbagliataImagePath: ''
  });
  const [randomOrder, setRandomOrder] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const history = useHistory();

  useEffect(() => {
    fetch(`/task2`)
      .then(response => response.json())
      .then(data => {
        setImagePaths({
          giustaImagePath: data.giustaImagePath,
          sbagliataImagePath: data.sbagliataImagePath
        });
        
        // Generate a random order
        const order = Math.random() < 0.5 ? ['giusta', 'sbagliata'] : ['sbagliata', 'giusta'];
        setRandomOrder(order);
      })
      .catch(error => {
        console.error('Error fetching image paths:', error);
      });
  }, [index]);

  const handleImageClick = (isCorrect) => {
    if (isCorrect) {
      history.push('/task3');
    } else {
      setErrorMessage('No è sbagliato, riprova');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h1>Task2</h1>
      <p>Bisogna premere l'immagine che più si alla metafora!</p>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', marginTop: '20px' }}>
        {randomOrder[0] === 'giusta' && imagePaths.giustaImagePath && (
          <img
            src={imagePaths.giustaImagePath}
            alt={`Giusta ${index}`}
            style={{ width: '200px', height: 'auto', cursor: 'pointer' }}
            onClick={() => handleImageClick(true)}
          />
        )}
        {randomOrder[0] === 'sbagliata' && imagePaths.sbagliataImagePath && (
          <img
            src={imagePaths.sbagliataImagePath}
            alt={`Sbagliata ${index}`}
            style={{ width: '200px', height: 'auto', cursor: 'pointer' }}
            onClick={() => handleImageClick(false)}
          />
        )}
        {randomOrder[1] === 'giusta' && imagePaths.giustaImagePath && (
          <img
            src={imagePaths.giustaImagePath}
            alt={`Giusta ${index}`}
            style={{ width: '200px', height: 'auto', cursor: 'pointer' }}
            onClick={() => handleImageClick(true)}
          />
        )}
        {randomOrder[1] === 'sbagliata' && imagePaths.sbagliataImagePath && (
          <img
            src={imagePaths.sbagliataImagePath}
            alt={`Sbagliata ${index}`}
            style={{ width: '200px', height: 'auto', cursor: 'pointer' }}
            onClick={() => handleImageClick(false)}
          />
        )}
      </div>
      {errorMessage && <p style={{ color: 'red', marginTop: '20px' }}>{errorMessage}</p>}
    </div>
  );
};

export default Task2;
